const clipboard={text:{async copy(t){await navigator.clipboard.writeText(t)}}};document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-clipboard-copy]").forEach(t=>{t.addEventListener("click",t=>{let e=t.target.getAttribute("data-clipboard-copy").split(","),a=document.querySelector(e[0])[e[1]];clipboard.text.copy(a)})})});